       const createProduct = async (req, res) => {
	const {
		name,
		price,
		image,
		brand,
		countInStock,
		numReviews,
		description,
		category
	} = req.body;

	let product = new Product({
		name,
		price,
		user: req.body.user,
		image,
		brand,
		countInStock,
		numReviews,
		description,
		category
	});
	const createProduct = await product.save();
	res.status(201).json(createProduct);
};

